import React from 'react';
import './App.css';
import Button from './Components/Button';
import FieldsetDados from './Components/FieldsetDados';
import FieldsetTrabalho from './Components/FieldsetTrabalho';

const estadoInicial = {
    nome: '',
    email: '',
    cpf: '',
    endereco: '',
    cidade: '',
    estado: 'selecione',
    tipo: 'house',
    resumo: '',
    cargo: '',
    descricaoCargo: '',
    erro: {},
    submitted: false,
}


class App extends React.Component {
  constructor() {
    super()

    this.state = estadoInicial;
  }

  validandoField = (item, value) => {
    switch (item) {
      case 'email':
        const valido = value.match(/\S+@\S+\.\S+/)
        return valido ? '' : ' '
        break;

      default:
        break;
    }
  }

  updateState = (name, value) => {
    this.setState((state) => ({
      [name]: value,

      erro: {
        ...state.erro,
        [name]: this.validandoField(name, value)
      }
    }))
  } 

  handleChange = e => {
    let { name, value } = e.target;

    if (name === 'nome') value = value.toUpperCase();

    this.updateState(name, value);
  }

  render() {
  return (
    <form className='App'>
      <FieldsetDados />
      <FieldsetTrabalho />
      <Button />
    </form>
  );
}}

export default App;
